import React, { Component } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { auth, db } from "../firebase/config";

import Clock from "react-live-clock";
import moment from "moment";
import "../css/Payment.css";

export default class Profile extends Component {
  constructor() {
    super();
    this.state = {
      user: auth().currentUser,
      notes: [],
      note: {},

      Inbound: "",
      Outbound: "",
      TimeIn: "",
      TimeOut: "",
      Price: "",
    };
    this.handleIN = this.handleIN.bind(this);
    this.handleOUT = this.handleOUT.bind(this);
    this.handleTimeIN = this.handleTimeIN.bind(this);
    this.handleTimeOUT = this.handleTimeOUT.bind(this);
    this.handlePrice = this.handlePrice.bind(this);

    this.createNote = this.createNote.bind(this);
  }

  componentDidMount() {
    db.ref(`user/${this.state.user.uid}/history`).on("value", (snapshot) => {
      let allNotes = [];
      snapshot.forEach((snap) => {
        allNotes.push(snap.val());
      });
      console.log(allNotes);
      this.setState({ notes: allNotes });
    });
  }

  handleIN(e) {
    this.setState({
      Inbound: e.target.value,
    });
  }

  handleOUT(e) {
    this.setState({
      Outbound: e.target.value,
    });
  }

  handleTimeIN(e) {
    this.setState({
      TimeIn: e.target.value,
    });
  }

  handleTimeOUT(e) {
    this.setState({
      TimeOut: e.target.value,
    });
  }

  handlePrice(e) {
    this.setState({
      Price: e.target.value,
    });
  }

  createNote() {
    const uid = this.state.user.uid;
    const { Inbound, Outbound, TimeIn, TimeOut, Price } = this.state;
    const unix_id = `unix-${Date.now()}`; // Unix Timestamp https://www.unixtimestamp.com
    const date = `${moment().format("MMMM Do YYYY, h:mm:ss a")}`;
    db.ref(`user/${uid}/history/${unix_id}`)
      .set({
        Inbound,
        Outbound,
        TimeIn,
        TimeOut,
        Price,
        date,
      })
      .then((_) => {
        this.setState({
          Inbound: "",
          Outbound: "",
          TimeIn: "",
          TimeOut: "",
          Price: "",
        });
      })
      .catch((error) => console.log(error.message));
  }

  deleteNote(note_id) {
    db.ref(`user/${this.state.user.uid}/history/${note_id}`).remove();
  }

  render() {
    return (
      <div>
        <Header />
        <div className="mx-4">
          <p>
            Login in as: <strong>{this.state.user.email}</strong>
          </p>
          <p>
            Current Time:{" "}
            <strong>
              <Clock format={"HH:mm:ss"} ticking={true} />
            </strong>
          </p>
        </div>

        <div class="container">
    <div class="row">
        <div class="col-xs-12 col-md-4">
            <div class="panel panel-default">
                <div class="panel-heading">
                    <h3 class="panel-title">
                        Payment Details
                    </h3>
                    <div class="checkbox pull-right">
                        <label>
                            <input type="checkbox" />
                            Remember
                        </label>
                    </div>
                </div>
                <div class="panel-body">
                    <form role="form">
                    <div class="form-group">
                        <label for="cardNumber">
                            CARD NUMBER</label>
                        <div class="input-group">
                            <input type="text" class="form-control" id="cardNumber" placeholder="Valid Card Number"
                                required autofocus />
                            <span class="input-group-addon"><span class="glyphicon glyphicon-lock"></span></span>
                        </div>
                    </div>
                    <div class="row">
                        <div class="col-xs-7 col-md-7">
                            <div class="form-group">
                                <label for="expityMonth">
                                    EXPIRY DATE</label>
                                <div class="col-xs-6 col-lg-6 pl-ziro">
                                    <input type="text" class="form-control" id="expityMonth" placeholder="MM" required />
                                </div>
                                <div class="col-xs-6 col-lg-6 pl-ziro">
                                    <input type="text" class="form-control" id="expityYear" placeholder="YY" required /></div>
                            </div>
                        </div>
                        <div class="col-xs-5 col-md-5 pull-right">
                            <div class="form-group">
                                <label for="cvCode">
                                    CV CODE</label>
                                <input type="password" class="form-control" id="cvCode" placeholder="CV" required />
                            </div>
                        </div>
                    </div>
                    </form>
                </div>
            </div>
            <ul class="nav nav-pills nav-stacked">
                <li class="active"><a href="#"><span class="badge pull-right"><span class="glyphicon glyphicon-usd"></span>4200</span> Final Payment</a>
                </li>
            </ul>
            <br/>
            <a href="http://www.jquery2dotnet.com" class="btn btn-success btn-lg btn-block" role="button">Pay</a>
        </div>
    </div>
</div>

        <Footer />
      </div>
    );
  }
}
