import React, { Component } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { auth, db } from "../firebase/config";

export default class Profile extends Component {
  constructor() {
    super();
    this.state = {
      user: auth().currentUser,
      notes: [],
      note: {},
      Inbound: "",

      // Outbound: "",
    };
    this.handleIN = this.handleIN.bind(this);
    // this.handleOUT = this.handleOUT.bind(this);

    this.createNote = this.createNote.bind(this);
  }

  componentDidMount() {
    db.ref(`History/${this.state.user.uid}`).on("value", (snapshot) => {
      let allNotes = [];
      snapshot.forEach((snap) => {
        allNotes.push(snap.val());
      });
      console.log(allNotes);
      this.setState({ notes: allNotes });
    });
  }

  handleIN(e) {
    this.setState({
      Inbound: e.target.value,
    });
  }

  // handleOUT(e) {
  //   this.setState({
  //     Outbound: e.target.value,
  //   });
  // }

  // handleTimeIN(e) {
  //   this.setState({
  //     content: e.target.value,
  //   });
  // }

  // handleTimeOUT(e) {
  //   this.setState({
  //     content: e.target.value,
  //   });
  // }

  // handleDate(e) {
  //   this.setState({
  //     content: e.target.value,
  //   });
  // }

  // handlePrice(e) {
  //   this.setState({
  //     content: e.target.value,
  //   });
  // }

  createNote() {
    const uid = this.state.user.uid;
    const { Inbound } = this.state;
    const note = this.state.note;
    // if (note && note.Inbound) {
    //   return db
    //     .ref(`History/${uid}/${note.note_id}`)
    //     .update({
    //       Inbound,
    //       uid,
    //     })
    //     .then((_) => {
    //       this.setState({ Inbound: "", note: {} });
    //     })
    //     .catch((error) => console.log(error.message));
    // }
    const note_id = `unix-${Date.now()}`;  // Unix Timestamp
    db.ref(`History/${uid}/${note_id}`)
      .set({
        Inbound,
        note_id,
        uid,
      })
      .then((_) => {
        this.setState({ Inbound: "" });
      })
      .catch((error) => console.log(error.message));
  }

  deleteNote(note_id) {
    db.ref(`History/${this.state.user.uid}/${note_id}`).remove();
  }

  render() {
    return (
      <div>
        <Header />
        <div className="mx-4">
          Login in as: <strong>{this.state.user.email}</strong>
        </div>
        {this.state.notes.map((note) => {
          return (
            <div className="card card-body shadow-sm m-4">
              <p>{note.Inbound}</p>
              <div class="ml-auto mr-auto">
                <button
                  className="btn-round btn btn-danger"
                  onClick={() => this.deleteNote(note.note_id)}
                >
                  Delete
                </button>
              </div>
            </div>
          );
        })}
        <hr/>
        <div className="form-group mx-4">
          <div className="row">
            <div className="col-md-3">
              <input
                className="form-control"
                placeholder="Inbound"
                onChange={this.handleIN}
                value={this.state.Inbound}
              />
            </div>
            <div className="col-md-2">
              <input
                className="form-control"
                placeholder="Outbound"
                // onChange={this.handleIN}
                // value={this.state.content}
              />
            </div>
            <div className="col-md-2">
              <input
                className="form-control"
                placeholder="Price"
                // onChange={this.handleIN}
                // value={this.state.content}
              />
            </div>
          </div>

          <div className="row my-2">
            <div className="col-md-2">
              <input
                className="form-control"
                placeholder="Time In"
                // onChange={this.handleIN}
                // value={this.state.content}
              />
            </div>
            <div className="col-md-2">
              <input
                className="form-control"
                placeholder="Time Out"
                // onChange={this.handleIN}
                // value={this.state.content}
              />
            </div>
          </div>

          <button className="btn btn-success mt-3" onClick={this.createNote}>
            Create Note
          </button>
        </div>
        <Footer />
      </div>
    );
  }
}
