import React, { Component } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
// import { auth, db } from "../firebase/config";
import ReactMapGL, { Marker } from "react-map-gl";

import markerIcon from "../images/Marker.png";
import "../css/Map.css";

export default class Map extends Component {
  constructor() {
    super();
    this.state = {
      viewport: {
        latitude: 13.745627822680577,
        longitude: 100.53415690427192,
        zoom: 15,
      },

      latit: 13.745627822680577,
      longit: 100.53415690427192,
    };
    this.handleLa = this.handleLa.bind(this);
    this.handleLo = this.handleLo.bind(this);
  }

  handleLa(e) {
    this.setState({
        latitude: e.target.value,
    });
  }

  handleLo(e) {
    this.setState({
        longit: e.target.value,
    });
  }

  render() {
    return (
      <div>
        <Header />
        <div>
          <ReactMapGL
            {...this.state.viewport}
            width="100vw"
            height="50vh"
            mapboxApiAccessToken={process.env.REACT_APP_MAPBOX_TOKEN}
            mapStyle="mapbox://styles/mapbox/streets-v11"
            onViewportChange={(viewport) => this.setState({ viewport })}
          >
            <Marker
              latitude={13.745627822680577}
              longitude={100.53415690427192}
              offsetLeft={-20}
              offsetTop={-40}
            >
              <a
                href="https://www.siamparagon.co.th"
                target="_blank"
                rel="noreferrer"
              >
                <img src={markerIcon} className="marker-icon" alt="marker" />
              </a>
            </Marker>
          </ReactMapGL>
        </div>

        <div className="form-group">
          <div className="row m-2">
            <div className="col-md-3">
              <label>Station Select</label>
              <select class="form-control">
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
              </select>
            </div>
          </div>
        </div>

        <hr />

        <div className="form-group">
          <div className="row m-2">
            <div className="col-md-3">
              <input
                className="form-control"
                placeholder="Latitude"

                onChange={this.handleIN}
                value={this.state.latit}
              />
            </div>
          </div>
          <div className="row m-2">
            <div className="col-md-3">
              <input
                className="form-control"
                placeholder="Longitude"

                onChange={this.handleIN}
                value={this.state.longit}
              />
            </div>
          </div>
        </div>

        <Footer />
      </div>
    );
  }
}
