import React, { Component } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { auth, db } from "../firebase/config";

import Clock from "react-live-clock";
import moment from "moment";
import "../css/Payment.css";

export default class Profile extends Component {
  constructor() {
    super();
    this.state = {
      user: auth().currentUser,
      notes: [],
      note: {},

      Phone: "",
      Amount: "",
    };
    this.handlePhone = this.handlePhone.bind(this);
    this.handleAmount = this.handleAmount.bind(this);

    this.createNote = this.createNote.bind(this);
  }

  //   componentDidMount() {
  //     db.ref(`user/${this.state.user.uid}/history`).on("value", (snapshot) => {
  //       let allNotes = [];
  //       snapshot.forEach((snap) => {
  //         allNotes.push(snap.val());
  //       });
  //       console.log(allNotes);
  //       this.setState({ notes: allNotes });
  //     });
  //   }

  handlePhone(e) {
    this.setState({
      Phone: e.target.value,
    });
  }

  handleAmount(e) {
    this.setState({
      Amount: e.target.value,
    });
  }

  createNote() {
    const uid = this.state.user.uid;
    const { Phone, Amount } = this.state;
    const unix_id = `unix-${Date.now()}`; // Unix Timestamp https://www.unixtimestamp.com
    const date = `${moment().format("MMMM Do YYYY, h:mm:ss a")}`;
    db.ref(`user/${uid}/history/${unix_id}`)
      .set({
        Phone,
        Outbound,
        TimeIn,
        TimeOut,
        Price,
        date,
      })
      .then((_) => {
        this.setState({
          Inbound: "",
          Outbound: "",
          TimeIn: "",
          TimeOut: "",
          Price: "",
        });
      })
      .catch((error) => console.log(error.message));
  }

  deleteNote(note_id) {
    db.ref(`user/${this.state.user.uid}/history/${note_id}`).remove();
  }

  render() {
    return (
      <div>
        <Header />
        <div className="mx-4">
          <p>
            Login in as: <strong>{this.state.user.email}</strong>
          </p>
          <p>
            Current Time:{" "}
            <strong>
              <Clock format={"HH:mm:ss"} ticking={true} />
            </strong>
          </p>
        </div>

        <div className="container col-md-6">
          <div className="card card-signin my-5">
            <div className="card-header">
              <h5 className="card-title">Payment Method</h5>
            </div>
            <div className="card-body">
              <div className="row">
                <div className="col-md-6">
                  <div className="form-group">
                    <label>Email Address</label>
                    <input
                      placeholder={this.state.user.email}
                      disabled
                      type="email"
                      className="form-control"
                    />
                  </div>
                </div>
                <div className="col-md-6">
                  <div className="form-group">
                    <label>Phone Number</label>
                    <input
                      placeholder="Number"
                      type="number"
                      className="form-control"
                      onChange={this.handleID}
                      value={this.state.id}
                      required
                    />
                  </div>
                </div>
              </div>
              <div className="row">
                <div className="col-md-12">
                  <div className="form-group">
                    <label>Amount</label>
                    <input
                      placeholder="#"
                      type="number"
                      className="form-control"
                      onChange={this.handleAddress}
                      value={this.state.address}
                    />
                  </div>
                </div>
              </div>

              <div class="row">
                <div class="ml-auto mr-auto">
                  <button
                    class="btn-round btn btn-primary mx-1"
                    onClick={this.createInfo}
                  >
                    Submit
                  </button>
                  <button
                    class="btn-round btn btn-primary mx-1"
                    onClick={this.createInfo}
                  >
                    PromptPay
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>

        <Footer />
      </div>
    );
  }
}
