import React, { Component } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
// import { auth, db } from "../firebase/config";
import ReactMapGL, { Marker } from "react-map-gl";

import markerIcon from "../images/Marker.png";
import "../css/Map.css";

export default class Map extends Component {
  constructor() {
    super();
    this.state = {
      viewport: {
        latitude: 13.745627822680577,
        longitude: 100.53415690427192,
        zoom: 15,
      },
      station: "",
      lati: "",
      longi: "",
    };
  }

  render() {
    return (
      <div>
        <Header />
        <div>
          <ReactMapGL
            {...this.state.viewport}
            width="100vw"
            height="50vh"
            mapboxApiAccessToken={process.env.REACT_APP_MAPBOX_TOKEN}
            mapStyle="mapbox://styles/mapbox/streets-v11"
            onViewportChange={(viewport) => this.setState({ viewport })}
          >
            <Marker
              latitude={parseFloat(value1)}
              longitude={parseFloat('100.53415690427192')}
              offsetLeft={-20}
              offsetTop={-40}
            >
              <a
                href="https://www.siamparagon.co.th"
                target="_blank"
                rel="noreferrer"
              >
                <img src={markerIcon} className="marker-icon" alt="marker" />
              </a>
            </Marker>
          </ReactMapGL>
        </div>

        <div className="form-group">
          <div className="row m-2">
            <div className="col-md-3">
              <strong>Station Select</strong>
              <select class="form-control m-2">
                <option
                    value1="13.745627822680577" value2="">Siam</option>
                <option>14</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
              </select>
            </div>
          </div>
        </div>

        <Footer />
      </div>
    );
  }
}
