import React, { Component } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { auth, db } from "../firebase/config";

import "../css/Profile.css";

export default class Profile extends Component {
  constructor() {
    super();
    this.state = {
      user: auth().currentUser,
      notes: [],
      content: "",
      note: {},
    };
    this.handleChange = this.handleChange.bind(this);
    this.createNote = this.createNote.bind(this);
    this.editNote = this.editNote.bind(this);
  }

  componentDidMount() {
    db.ref(`all_notes/${this.state.user.uid}`).on("value", (snapshot) => {
      let allNotes = [];
      snapshot.forEach((snap) => {
        allNotes.push(snap.val());
      });
      console.log(allNotes);
      this.setState({ notes: allNotes });
    });
  }

  handleChange(e) {
    this.setState({
      content: e.target.value,
    });
  }

  createNote() {
    const uid = this.state.user.uid;
    const { content } = this.state;
    const note = this.state.note;
    if (note && note.content) {
      return db
        .ref(`all_notes/${uid}/${note.note_id}`)
        .update({
          content,
          uid,
        })
        .then((_) => {
          this.setState({ content: "", note: {} });
        })
        .catch((error) => console.log(error.message));
    }
    const note_id = `note-${Date.now()}`;
    db.ref(`all_notes/${uid}/${note_id}`)
      .set({
        content,
        note_id,
        uid,
      })
      .then((_) => {
        this.setState({ content: "" });
      })
      .catch((error) => console.log(error.message));
  }

  editNote(note_id) {
    db.ref(`all_notes/${this.state.user.uid}/${note_id}`)
      .once("value")
      .then((snapshot) => {
        this.setState({
          note: snapshot.val(),
          content: snapshot.val().content,
        });
      });
  }

  deleteNote(note_id) {
    db.ref(`all_notes/${this.state.user.uid}/${note_id}`).remove();
  }

  render() {
    return (
      <div>
        <Header />
        <div className="mx-4">
          Login in as: <strong>{this.state.user.email}</strong>
        </div>
        {this.state.notes.map((note) => {
          return (
            <p>
                Inbound : {note.Inbound} | {note.TimeIn}
            </p>
          );
        })}
        <div className="container col-md-8">
          <div className="card card-signin my-5">
            <div className="card-header">
              <h5 className="card-title">Profile Info</h5>
            </div>
            <div className="card-body">
              <form>
                <div className="row">
                  <div className="pr-1 col-md-6">
                    <div className="form-group">
                      <label>
                        First Name <em className="em-color">*</em>
                      </label>
                      <input
                        placeholder="First Name"
                        type="text"
                        className="form-control"

                        // required
                      />
                    </div>
                  </div>
                  <div className="pl-1 col-md-6">
                    <div className="form-group">
                      <label>
                        Last Name <em className="em-color">*</em>
                      </label>
                      <input
                        placeholder="Last Name"
                        type="text"
                        className="form-control"

                        // required
                      />
                    </div>
                  </div>
                </div>
                <div className="row">
                  <div className="col-md-4">
                    <div className="form-group">
                      <label>
                        ID No. <em className="em-color">*</em>
                      </label>
                      <input
                        placeholder="No."
                        type="text"
                        className="form-control"

                        // required
                      />
                    </div>
                  </div>
                  <div className="px-1 col-md-5">
                    <div className="form-group">
                      <label>
                        Email Address <em className="em-color">*</em>
                      </label>
                      <input
                        placeholder="Email"
                        type="email"
                        className="form-control"
                        required
                      />
                    </div>
                  </div>
                  <div className="pl-1 col-md-3">
                    <div className="form-group">
                      <label>
                        Password <em className="em-color">*</em>
                      </label>
                      <input
                        placeholder="Password"
                        type="text"
                        className="form-control"
                        required
                      />
                    </div>
                  </div>
                </div>
                <div className="row">
                  <div className="col-md-12">
                    <div className="form-group">
                      <label>Address</label>
                      <input
                        placeholder="Address"
                        type="text"
                        className="form-control"
                      />
                    </div>
                  </div>
                </div>
                <div className="row">
                  <div className="pr-1 col-md-4">
                    <div className="form-group">
                      <label>City</label>
                      <input
                        placeholder="City"
                        type="text"
                        className="form-control"
                      />
                    </div>
                  </div>
                  <div className="px-1 col-md-4">
                    <div className="form-group">
                      <label>Country</label>
                      <input
                        placeholder="Country"
                        type="text"
                        className="form-control"
                      />
                    </div>
                  </div>
                  <div className="pl-1 col-md-4">
                    <div className="form-group">
                      <label>Postal Code</label>
                      <input
                        placeholder="ZIP Code"
                        type="number"
                        className="form-control"
                      />
                    </div>
                  </div>
                </div>
                <div class="row">
                  <div class="ml-auto mr-auto">
                    <button class="btn-round btn btn-primary" type="button">
                      Register
                    </button>
                  </div>
                </div>
                <div class="row my-2">
                  <div class="ml-auto mr-auto">
                    <button class="btn-round btn btn-danger">Delete</button>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
        <Footer />
      </div>
    );
  }
}
