import React, { Component } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { auth, db } from "../firebase/config";

import "../css/Profile.css";

export default class Profile extends Component {
  constructor() {
    super();
    this.state = {
      user: auth().currentUser,
      notes: [],
      note: {},

      Inbound: "",
      Outbound: "",
      TimeIn: "",
      TimeOut: "",
      Price: "",
    };
    this.handleIN = this.handleIN.bind(this);
    this.handleOUT = this.handleOUT.bind(this);
    this.handleTimeIN = this.handleTimeIN.bind(this);
    this.handleTimeOUT = this.handleTimeOUT.bind(this);
    this.handlePrice = this.handlePrice.bind(this);

    this.createNote = this.createNote.bind(this);
  }

  componentDidMount() {
    db.ref(`Station/${this.state.user.uid}`).on("value", (snapshot) => {
      let allNotes = [];
      snapshot.forEach((snap) => {
        allNotes.push(snap.val());
      });
      console.log(allNotes);
      this.setState({ notes: allNotes });
    });
  }

  handleIN(e) {
    this.setState({
      Inbound: e.target.value,
    });
  }

  handleOUT(e) {
    this.setState({
      Outbound: e.target.value,
    });
  }

  handleTimeIN(e) {
    this.setState({
      TimeIn: e.target.value,
    });
  }

  handleTimeOUT(e) {
    this.setState({
      TimeOut: e.target.value,
    });
  }

  handlePrice(e) {
    this.setState({
      Price: e.target.value,
    });
  }

  createNote() {
    const uid = this.state.user.uid;
    const { Inbound, Outbound, TimeIn, TimeOut, Price } = this.state;
    const note_id = `unix-${Date.now()}`; // Unix Timestamp https://www.unixtimestamp.com
    db.ref(`History/${uid}/${note_id}`)
      .set({
        Inbound,
        Outbound,
        TimeIn,
        TimeOut,
        Price,

        note_id,
      })
      .then((_) => {
        this.setState({
          Inbound: "",
          Outbound: "",
          TimeIn: "",
          TimeOut: "",
          Price: "",
        });
      })
      .catch((error) => console.log(error.message));
  }

  editNote(note_id) {
    db.ref(`all_notes/${this.state.user.uid}/${note_id}`)
      .once("value")
      .then((snapshot) => {
        this.setState({
          note: snapshot.val(),
          content: snapshot.val().content,
        });
      });
  }

  deleteNote(note_id) {
    db.ref(`all_notes/${this.state.user.uid}/${note_id}`).remove();
  }

  render() {
    return (
      <div>
        <Header />
        <div className="mx-4">
          Login in as: <strong>{this.state.user.email}</strong>
        </div>
        {this.state.notes.map((note) => {
          return (
            // <p>
            //     Inbound : {note.Inbound} | {note.TimeIn}
            // </p>

            <div className="container col-md-8">
          <div className="card card-signin my-5">
            <div className="card-header">
              <h5 className="card-title">Profile Info</h5>
            </div>
            <div className="card-body">
              <form>
                <div className="row">
                  <div className="pr-1 col-md-6">
                    <div className="form-group">
                      <label>
                        First Name <em className="em-color">*</em>
                      </label>
                      <input
                        placeholder= {note.Inbound}
                        type="text"
                        className="form-control"

                        // required
                      />
                    </div>
                  </div>
                  <div className="pl-1 col-md-6">
                    <div className="form-group">
                      <label>
                        Last Name <em className="em-color">*</em>
                      </label>
                      <input
                        placeholder="Last Name"
                        type="text"
                        className="form-control"

                        // required
                      />
                    </div>
                  </div>
                </div>
                <div className="row">
                  <div className="col-md-4">
                    <div className="form-group">
                      <label>
                        ID No. <em className="em-color">*</em>
                      </label>
                      <input
                        placeholder="No."
                        type="text"
                        className="form-control"

                        // required
                      />
                    </div>
                  </div>
                  <div className="px-1 col-md-5">
                    <div className="form-group">
                      <label>
                        Email Address <em className="em-color">*</em>
                      </label>
                      <input
                        placeholder= {this.state.user.email} disabled
                        type="email"
                        className="form-control"
                      />
                    </div>
                  </div>
                  <div className="pl-1 col-md-3">
                    <div className="form-group">
                      <label>
                        Password <em className="em-color">*</em>
                      </label>
                      <input
                        placeholder="Password" disabled
                        type="text"
                        className="form-control"
                        required
                      />
                    </div>
                  </div>
                </div>
                <div className="row">
                  <div className="col-md-12">
                    <div className="form-group">
                      <label>Address</label>
                      <input
                        placeholder="Address"
                        type="text"
                        className="form-control"
                      />
                    </div>
                  </div>
                </div>
                <div className="row">
                  <div className="pr-1 col-md-4">
                    <div className="form-group">
                      <label>City</label>
                      <input
                        placeholder="City"
                        type="text"
                        className="form-control"
                      />
                    </div>
                  </div>
                  <div className="px-1 col-md-4">
                    <div className="form-group">
                      <label>Country</label>
                      <input
                        placeholder="Country"
                        type="text"
                        className="form-control"
                      />
                    </div>
                  </div>
                  <div className="pl-1 col-md-4">
                    <div className="form-group">
                      <label>Postal Code</label>
                      <input
                        placeholder="ZIP Code"
                        type="number"
                        className="form-control"
                      />
                    </div>
                  </div>
                </div>
                <div class="row">
                  <div class="ml-auto mr-auto">
                    <button class="btn-round btn btn-primary" type="button">
                      Update
                    </button>
                  </div>
                </div>
                <div class="row my-2">
                  <div class="ml-auto mr-auto">
                    <button class="btn-round btn btn-danger">Delete</button>
                  </div>
                </div>
              </form>
            </div>
          </div>
        </div>
          );
        })}
        <Footer />
      </div>
    );
  }
}
