import React, { Component } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { auth, db } from "../firebase/config";

export default class Profile extends Component {
  constructor() {
    super();
    this.state = {
      user: auth().currentUser,
      notes: [],
      note: {},
      content: "",
    };
    this.handleIN = this.handleIN.bind(this);

    this.createNote = this.createNote.bind(this);
  }

  componentDidMount() {
    db.ref(`History/${this.state.user.uid}`).on("value", (snapshot) => {
      let allNotes = [];
      snapshot.forEach((snap) => {
        allNotes.push(snap.val());
      });
      console.log(allNotes);
      this.setState({ notes: allNotes });
    });
  }

  handleIN(e) {
    this.setState({
      content: e.target.value,
    });
  }

  // handleOUT(e) {
  //   this.setState({
  //     content: e.target.value,
  //   });
  // }

  // handleTimeIN(e) {
  //   this.setState({
  //     content: e.target.value,
  //   });
  // }

  // handleTimeOUT(e) {
  //   this.setState({
  //     content: e.target.value,
  //   });
  // }

  // handleDate(e) {
  //   this.setState({
  //     content: e.target.value,
  //   });
  // }

  // handlePrice(e) {
  //   this.setState({
  //     content: e.target.value,
  //   });
  // }

  createNote() {
    const uid = this.state.user.uid;
    const { content } = this.state;
    const note = this.state.note;
    if (note && note.content) {
      return db
        .ref(`History/${uid}/${note.note_id}`)
        .update({
          content,
          uid,
        })
        .then((_) => {
          this.setState({ content: "", note: {} });
        })
        .catch((error) => console.log(error.message));
    }
    const note_id = `note-${Date.now()}`;
    db.ref(`History/${uid}/${note_id}`)
      .set({
        content,
        note_id,
        uid,
      })
      .then((_) => {
        this.setState({ content: "" });
      })
      .catch((error) => console.log(error.message));
  }

  deleteNote(note_id) {
    db.ref(`History/${this.state.user.uid}/${note_id}`).remove();
  }

  render() {
    return (
      <div>
        <Header />
        <div className="mx-4">
          Login in as: <strong>{this.state.user.email}</strong>
        </div>
        {this.state.notes.map((note) => {
          return (
            <div key={note.note_id} className="card card-body shadow-sm m-4">
              <p>{note.content}</p>
              <div class="ml-auto mr-auto">
                <button
                  className="btn-round btn btn-danger"
                  onClick={() => this.deleteNote(note.note_id)}
                >
                  Delete
                </button>
              </div>
            </div>
          );
        })}
        <div className="form-group mx-4">
          <div className="row">
            <div className="col-md-2">
              <input
                className="form-control"
                placeholder="Inbound"
                onChange={this.handleIN}
                value={this.state.content}
              />
            </div>
            <div className="px-0 col-md-2">
              <input
                className="form-control"
                placeholder="Outbound"
                // onChange={this.handleIN}
                // value={this.state.content}
              />
            </div>
          </div>

          <div className="row">
            <div className="col-md-2">
              <input
                className="form-control"
                placeholder="Inbound"
                onChange={this.handleIN}
                value={this.state.content}
              />
            </div>
            <div className="px-0 col-md-2">
              <input
                className="form-control"
                placeholder="Outbound"
                // onChange={this.handleIN}
                // value={this.state.content}
              />
            </div>
          </div>

          <button className="btn btn-success mt-3" onClick={this.createNote}>
            Create Note
          </button>
        </div>
        <Footer />
      </div>
    );
  }
}
