import React, { Component } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { auth, db } from "../firebase/config";

import Clock from "react-live-clock"
import moment from "moment"
import "../css/Payment.css";

export default class Profile extends Component {
  constructor() {
    super();
    this.state = {
      user: auth().currentUser,
      notes: [],
      note: {},

      Inbound: "",
      Outbound: "",
      TimeIn: "",
      TimeOut: "",
      Price: ""
    };
    this.handleIN = this.handleIN.bind(this);
    this.handleOUT = this.handleOUT.bind(this);
    this.handleTimeIN = this.handleTimeIN.bind(this);
    this.handleTimeOUT = this.handleTimeOUT.bind(this);
    this.handlePrice = this.handlePrice.bind(this);

    this.createNote = this.createNote.bind(this);
  }

  componentDidMount() {
    db.ref(`user/${this.state.user.uid}/history`).on("value", (snapshot) => {
      let allNotes = [];
      snapshot.forEach((snap) => {
        allNotes.push(snap.val());
      });
      console.log(allNotes);
      this.setState({ notes: allNotes });
    });
  }

  handleIN(e) {
    this.setState({
      Inbound: e.target.value,
    });
  }

  handleOUT(e) {
    this.setState({
      Outbound: e.target.value,
    });
  }

  handleTimeIN(e) {
    this.setState({
      TimeIn: e.target.value,
    });
  }

  handleTimeOUT(e) {
    this.setState({
      TimeOut: e.target.value,
    });
  }

  handlePrice(e) {
    this.setState({
      Price: e.target.value,
    });
  }

  createNote() {
    const uid = this.state.user.uid;
    const { Inbound, Outbound, TimeIn, TimeOut, Price } = this.state;
    const unix_id = `unix-${Date.now()}`; // Unix Timestamp https://www.unixtimestamp.com
    const date = `${moment().format('MMMM Do YYYY, h:mm:ss a')}`;
    db.ref(`user/${uid}/history/${unix_id}`)
      .set({
        Inbound,
        Outbound,
        TimeIn,
        TimeOut,
        Price,
        date
      })
      .then((_) => {
        this.setState({
          Inbound: "",
          Outbound: "",
          TimeIn: "",
          TimeOut: "",
          Price: ""
        });
      })
      .catch((error) => console.log(error.message));
  }

  deleteNote(note_id) {
    db.ref(`user/${this.state.user.uid}/history/${note_id}`).remove();
  }

  render() {
    return (
      <div>
        <Header />
        <div className="mx-4">
          <p>
            Login in as: <strong>{this.state.user.email}</strong>
          </p>
          <p>
            Current Time: <strong><Clock format={"HH:mm:ss"} ticking={true}/></strong>
          </p>
        </div>

        <div class="container">
	<div class="row">
		<div class="paymentCont">
						<div class="headingWrap">
								<h3 class="headingTop text-center">Select Your Payment Method</h3>	
								<p class="text-center">Created with bootsrap button and using radio button</p>
						</div>
						<div class="paymentWrap">
							<div class="btn-group paymentBtnGroup btn-group-justified" data-toggle="buttons">
					            <label class="btn paymentMethod active">
					            	<div class="method visa"></div>
					                <input type="radio" name="options" checked /> 
					            </label>
					            <label class="btn paymentMethod">
					            	<div class="method master-card"></div>
					                <input type="radio" name="options" /> 
					            </label>
					            <label class="btn paymentMethod">
				            		<div class="method amex"></div>
					                <input type="radio" name="options" />
					            </label>
					             <label class="btn paymentMethod">
				             		<div class="method vishwa"></div>
					                <input type="radio" name="options" /> 
					            </label>
					            <label class="btn paymentMethod">
				            		<div class="method ez-cash"></div>
					                <input type="radio" name="options" /> 
					            </label>

					        </div>        
						</div>
						<div class="footerNavWrap clearfix">
							<div class="btn btn-success pull-left btn-fyi"><span class="glyphicon glyphicon-chevron-left"></span> CONTINUE SHOPPING</div>
							<div class="btn btn-success pull-right btn-fyi">CHECKOUT<span class="glyphicon glyphicon-chevron-right"></span></div>
						</div>
					</div>

	</div>
</div>

        <Footer />
      </div>
    );
  }
}
