import React, { Component } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { db } from "../firebase/config";
import ReactMapGL, { Marker } from "react-map-gl";

import markerIcon from "../images/Marker.png";
import "../css/Map.css";

export default class Map extends Component {
  constructor() {
    super();
    this.state = {
      viewport: {
        latitude: 13.745627822680577,
        longitude: 100.53415690427192,
        zoom: 15,
      },
      station: "",
      notes: [],
      lati: "13.745627822680577",
      longi: "100.53415690427192",
    };
    this.handleSel = this.handleSel.bind(this);
  }

  componentDidMount() {
    db.ref(`station/${this.state.station}`).on("value", (snapshot) => {
      let allNotes = [];
      snapshot.forEach((snap) => {
        allNotes.push(snap.val());
      });
      console.log(allNotes);
      this.setState({ notes: allNotes });
    });
  }

  handleSel(e) {
    this.setState({
      station: e.target.value,
    });
  }

  render() {
    return (
      <div>
        <Header />
        <div>
          <ReactMapGL
            {...this.state.viewport}
            width="100vw"
            height="50vh"
            mapboxApiAccessToken={process.env.REACT_APP_MAPBOX_TOKEN}
            mapStyle="mapbox://styles/mapbox/streets-v11"
            onViewportChange={(viewport) => this.setState({ viewport })}
          >
            <Marker
              latitude={parseFloat(this.state.lati)}
              longitude={parseFloat(this.state.longi)}
              offsetLeft={-20}
              offsetTop={-40}
            >
              <a
                href="https://www.siamparagon.co.th"
                target="_blank"
                rel="noreferrer"
              >
                <img src={markerIcon} className="marker-icon" alt="marker" />
              </a>
            </Marker>
          </ReactMapGL>
        </div>

        <div className="form-group">
          <div className="row m-2">
            <div className="col-md-3">
              <strong>Station Select</strong>
              <select
                class="form-control m-2"
                onChange={this.handleSel}
                value={this.state.station}
              >
                <option value="siam">Siam</option>
                <option value="asok">Asok</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
              </select>
            </div>
          </div>
        </div>

        <Footer />
      </div>
    );
  }
}
